import '../css/page/opidex.css';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Divider from '@mui/material/Divider';
import useEth from "../contexts/EthContext/useEth";
import Chip from '@mui/material/Chip';

function OpiDex() {
    const { state: { web3 ,accounts, owner, contractOPI ,contractOpiDex } } = useEth();

    const buyTokens = async (_amount) => {
        try {
            await contractOpiDex.methods.buyTokens().send({ from: accounts[0], value:web3.toWei(parseInt(_amount), "ether")} );
        } catch (err) {
            console.log(err);
        }
    }


  return (
    <div className="opidex">
       <Box sx={{ width: '100%' }}  display="flex"  flexDirection="column" justifyContent="center"
       >
        <Stack  direction="row"
        divider={<Divider orientation="vertical" flexItem />}
        spacing={2} sx={{ width: '100%' }}  >
        <TextField
          required
          id="outlined-required"
          type="number"
          label="Amout"
          defaultValue="Hello World"
        />


       </Stack>

      <Stack  direction="row"
        divider={<Divider orientation="vertical" flexItem />}
        spacing={2} >
        <TextField
          required
          id="outlined-required"
          type="number"
          label="Amout"
          defaultValue="Hello World"
        />

       <button className="opidexButton"  >Buy OPIs</button>
       </Stack>
    </Box>
    </div>
  );
}

export default OpiDex;
