import * as React from 'react';
import useEth from "../contexts/EthContext/useEth";
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardContent from '@mui/material/CardContent';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import { purple } from '@mui/material/colors';
import { pink } from '@mui/material/colors';
import Stack from '@mui/material/Stack';
import { WalletSharp} from '@mui/icons-material';


function CardPerson({ address }) {

    const { state: { owner } } = useEth();
    return (
        <div key="{address}" id="CardPerson_main">
            <Card >
                <CardHeader
                    // avatar=
                    // {owner === address ? <Avatar sx={{ bgcolor: purple[900] }} aria-label="recipe">
                    //     A
                    // </Avatar> :
                    //     <Avatar sx={{ bgcolor: pink[900] }} aria-label="recipe">
                    //         V
                    //     </Avatar>}

                    avatar={
                        <Avatar src="https://mui.com/static/images/avatar/2.jpg">
                        </Avatar>
                      }


                    title={address}
                    subheader={owner === address ? "OpiChain owner" : "Opichain member"}
                />

                <CardContent>
                <Stack direction="row" spacing={10}>
        <WalletSharp /> 
        Item 2

      </Stack>

                </CardContent>
            </Card>
        </div>
    );
}

export default CardPerson;
